function linkToArray(head){
  const res = [];
  let set = new Set();

  while(head) {
    if(set.has(head)) {
      res.push('loop!');
      return res;
    }

    res.push(head.val);    
    set.add(head);
    head = head.next;
  }

  return res;
}

