function reverseLink(head) {
  if(head === null) {
    return head;
  }

  let prev = head;

  let next = prev.next;
  prev.next = null;

  while(prev && next) {
    let temp = next.next;
    next.next = prev;
    prev = next;
    next = temp;

    if(next === head) {
